import "../styles/postCard.scss";
import React from "react";
import { axiosPrivate, Axios } from "../axios";
const PostCard = (props) => {
  const [image, setImage] = React.useState();

  const getImge = async () => {
    try {
      const data = await Axios.get(
        `http:127.0.0.1:8000/public/img/posts/${props.image}`
      );
      return data;
    } catch (error) {
      console.log(error);
    }
  };

  React.useEffect(() => {
    const image = getImge();
    setImage(image);
  }, []);
  return (
    <div className="PostCard">
      <div className="upper">
        <span className="Header">{props.heading}</span>
        {props.image && (
          <img
            src={image}
            //src={`http://127.0.0.1:8000/public/img/posts/${props.image}`}
            alt=""
          />
        )}
        <p className="title">{props.title}</p>
      </div>
      <div className="lover">
        <span className="Date">Date : {props.createdAt.split("T")[0]}</span>
        <span className="User">User : {props.user}</span>
      </div>
    </div>
  );
};

export default PostCard;
